import { styled } from '@/styles/theme';

import Icon from '@/components/atoms/img/Icon';
import Button from '@/components/atoms/button/Button';
import Text from '@/components/atoms/text/Text';

export const Container = styled.div`
  display: grid;
  place-items: center;
  min-height: 100vh;
  padding: 20px;
  background: ${({ theme }) => theme.colors.primary};
`;

export const HeroContainer = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const Img = styled(Icon)`
  width: 100%;
`;

export const LogoImg = styled(Img)`

`;

export const StyledSlogan = styled(Text)`
  font-size: 16px;
  text-align: center;
  margin: 10px 0 40px 0;

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    font-size: 26px;
  }
`;

export const HeroImg = styled(Img)`
  margin-bottom: 40px;
  width: 80%;

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    margin-bottom: 60px;
  }
`;

export const ButtonContainer = styled.section`
  padding: 0 20px;

  @media (min-width: ${({ theme }) => theme.breakpoints.extraSmallMin}) {
    & div:first-of-type {
      display: flex;
      justify-content: space-evenly;
    }
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    padding: 0;
  }
`;

export const StyledButton = styled(Button)`
  width: 100%;
  margin-bottom: 15px;

  @media (min-width: ${({ theme }) => theme.breakpoints.extraSmallMin}) {
    max-width: 40%;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    max-width: none;
  }
`;

export const StyledText = styled(Text)`
  display: flex;
  justify-content: center;
`;

export const StyledHeartIcon = styled(Icon)`
  margin-left: 10px;
`;
