import styled, { css } from 'styled-components';
import { shade } from 'polished';

export const Title = styled.h1`
  font-size: 48px;
  color: #3A3A3A;
  margin-top: 80px;
  max-width: 450px;
  line-height: 56px;
`;

export const Form = styled.form`
  display: flex;
  max-width: 700px;
  margin-top: 40px;
`;

export const FormItemStyles = css`
  height: 70px;
  border: 0;
`;

export const Input = styled.input`
  ${FormItemStyles}
  flex: 1;
  padding: 0 24px;
  border-radius: 5px 0 0 5px;
  color: #3a3a3a;

  &::placeholder {
    color: #a8a8b3;
  }
`;

export const SubmitButton = styled.button`
  ${FormItemStyles}
  width: 210px;
  background: #04d361;
  color: white;
  font-weight: bold;
  border-radius: 0 5px 5px 0;
  transition: .3s;

  &:hover {
    background: ${shade(0.2, '#04d361')};
  }
`;

export const RepositoriesList = styled.section`
  margin-top: 80px;
  max-width: 700px;
`;

export const RepositoriesListItem = styled.a`
  background: white;
  border-radius: 5px;
  width: 100%;
  padding: 24px;
  display: block;
  text-decoration: none;
  display: flex;
  align-items: center;
`;
