import styled from 'styled-components';

export const Container = styled.div``;

export const Header = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;

  a {
    display: flex;
    align-items: center;
    text-decoration: none;
    color: #a8a8b3;
    transition: .3;

    &:hover {
      color: #777;
    }

    svg {
      margin-right: 5px;
    }
  }
`;

export const RepositoryInfo = styled.section`
  margin-top: 80px;
`;

export const RepositoryInfoHeader = styled.header`
  display: flex;
  align-items: center;

  img {
    height: 125px;
    width: 125px;
    border-radius: 50%;
  }

  div {
    margin-left: 24px;

    strong {
      font-size: 36px;
      color: #3d3d4d;
    }

    p {
      font-size: 18px;
      color: #737380;
      margin-top: 4px;
    }
  }
`;

export const RepositoryInfoList = styled.ul`
  display: flex;
  margin-top: 40px;

  li {

    & + li {
      margin-left: 80px;
    }

    strong {
      color: #3d3d4d;
      display: block;
      font-size: 36px;
    }

    span {
      color: #6c6c80;
      display: block;
      margin-top: 4px;
    }
  }
`;

export const IssuesList = styled.div`
  margin-top: 80px;

  a {
    text-decoration: none;
    width: 100%;
    background: white;

    div {

      border-radius: 5px;
      padding: 24px;
      display: block;
      text-decoration: none;
      display: flex;
      align-items: center;
      flex: 1;
      transition: .3s;


      &:hover {
        transform: translateX(10px);
      }

      strong {
        display: block;
        font-size: 20px;
        color: #3d3d4d;
      }

      p {
        font-size: 18px;
        color: #a8a8b3;
        margin-top: 4px;
      }
    }

    svg {
      margin-left: auto;
      color: #cbcbd6;
      margin-left: 20px;
    }
  }
`;
