import React, { useEffect, useState } from 'react';
import Icon, { AvailableIcons } from '@/components/atoms/img/Icon';
import { Link } from 'react-router-dom';

import Text, { TextSize } from '@/components/atoms/text/Text';

import Button from '@/components/atoms/button/Button';
import {
  Container,
  Header,
  Navbar,
  HeaderTextContainer,
  Main,
  Form,
  ProffysList,
  ProffysListItemHeader,
  ProffysListItemText,
  ProffysListItemFooter,
} from './styles';

interface Proffy {
  name: string,
  subject: string,
  description: string,
  price: number,
  phone_number: string,
  img_url: string,
}

const Proffys: React.FC = () => {
  const [proffys, setProffys] = useState<Proffy[]>(handleProffysInitialValue);

  useEffect(() => {
    localStorage.setItem('@proffys:proffys', JSON.stringify(proffys));
  }, [proffys]);

  function handleProffysInitialValue() {
    const storagedProffys = localStorage.getItem('@proffys:proffys');
    return storagedProffys ? JSON.parse(storagedProffys) : [];
  }

  return (
    <Container>
      <Header>
        <Navbar>
          <Link className="arrow-left" to="/">
            <Icon iconName={AvailableIcons.arrowLeft} size={60} />
          </Link>
          <Icon iconName={AvailableIcons.logo} size={60} />
        </Navbar>

        <HeaderTextContainer>
          <h2>Estes são os</h2>
          <h2>proffys disponíveis</h2>
        </HeaderTextContainer>
      </Header>

      <Main>
        <Form />
        <ProffysList>
          {proffys.map((proffy, index) => (
            <li>
              <ProffysListItemHeader>
                <img src={proffy.img_url} alt="" />
                <div>
                  <h5>{proffy.name}</h5>
                  <ProffysListItemText>{proffy.subject}</ProffysListItemText>
                </div>
              </ProffysListItemHeader>

              <ProffysListItemText>{proffy.description}</ProffysListItemText>

              <ProffysListItemFooter>
                <div>
                  <Text size={TextSize.small}>Preço/hora</Text>
                  <h5>R$ {proffy.price}</h5>
                </div>

                <Button iconName={AvailableIcons.whatsapp}>Entrar em contato</Button>
              </ProffysListItemFooter>
            </li>
          ))}
        </ProffysList>
      </Main>
    </Container>
  );
};

export default Proffys;
