import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    outline: 0;
    box-sizing: border-box;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-size: 16px;
  }

  body, #root {
    min-height: 100vh;
  }

  ul, li {
    list-style: none;
  }

  button {
    cursor: pointer;
  }

  label, span {
    font-size: 14px;
  }

  small {
    font-size: 12px;
  }

  h2 {
    font-size: 36px;
  }

  h4 {
    font-size: 24px;
  }
`;
