import styled from 'styled-components';
import { darken, lighten } from 'polished';

export const Container = styled.div`
  background: ${({ theme }) => theme.colors.primary};
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px;
`;

export const Title = styled.h2`
  margin: 50px 0;
  color: ${({ theme }) => theme.colors.titleInPrimary};
  font-size: 2.3rem;
`;

export const Form = styled.form`
  position: relative;
  background: ${({ theme }) => lighten('0.3', theme.colors.primary)};
  border-radius: ${({ theme }) => theme.borderRadius};
  height: 50px;
  width: 100%;
  max-width: 620px;
  box-shadow: 1px 2px 5px ${({ theme }) => darken('0.1', theme.colors.primary)};
  transition: .2s;

  &:finput-ocus {
    background: ${({ theme }) => theme.colors.inputBackground};
  }
`;

export const Input = styled.input`
  background: transparent;
  border: none;
  height: inherit;
  width: 100%;
  padding: 0 90px 0 15px;
  font-size: 1rem;
  font-weight: 500;
  color: ${({ theme }) => theme.colors.inputText};

  &:focus-within {
    background: ${({ theme }) => theme.colors.inputBackground};
  }
`;

export const SubmitButton = styled.button`
  position: absolute;
  top: 6%;
  right: 1%;
  background: ${({ theme }) => theme.colors.primary};
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius};
  color: ${({ theme }) => theme.colors.textInPrimary};
  font-weight: 500;
  padding: 12px;
  font-size: 1rem;
`;

export const TodoList = styled.ul``;

export const TodoListItem = styled.li``;

export const Checkbox = styled.input``;

export const TodoDescription = styled.span``;
