import React, { useState, FormEvent } from 'react';
import { FiChevronRight } from 'react-icons/fi';

import repositoriesApi from '../../services/api/repositories_api';

import logoImg from '../../assets/logo.svg';

import {
  Cotainer,
  Title,
  Form,
  Input,
  SubmitButton,
  RepositoriesList,
  RepositoriesListItem,
  RepositoriesListItemRepoInfo,
} from './styles';

interface IRepository {
  full_name: string,
  description: string,
  owner: {
    login: string,
    avatar_url: string,
  }
}

const Dashboard: React.FC = () => {
  const [inputRepository, setInputRepository] = useState('');
  const [repositories, setRepositories] = useState<IRepository[]>([]);

  async function addRepository(event: FormEvent<HTMLFormElement>) {
    event.preventDefault();
    const { data } = await repositoriesApi.get<IRepository>(`/repos/${inputRepository}`);
    setRepositories([...repositories, data]);
  }

  return (
    <Cotainer>
      <img src={logoImg} alt="Github Explorer" />
      <Title>Explore repositórios do Github</Title>

      <Form onSubmit={addRepository}>
        <Input
          value={inputRepository}
          onChange={({ target }) => setInputRepository(target.value)}
          placeholder="Digite o nome do repositório"
        />
        <SubmitButton>Pesquisar</SubmitButton>
      </Form>

      <RepositoriesList>
        {}
      </RepositoriesList>
    </Cotainer>
  );
};

export default Dashboard;
