import { styled, ThemeColors } from '@/styles/theme';

export interface ContainerProps { background?: ThemeColors }

export const Container = styled.button<ContainerProps>`
  display: flex;
  align-items: center;
  line-height: 26px;
  color: white;
  font-weight: bold;
  background: ${({ background }) => background};
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius};
  height: 56px;
  padding: 0 40px;
  line-height: 26px;
`;
