import React, { useEffect, useState } from 'react';
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import { useRouteMatch, Link } from 'react-router-dom';
import repositoriesApi from '../../services/api/repositories_api';

import logoImg from '../../assets/logo.svg';

import {
  Header,
  ContentContainer,
  RepositoryInfo,
  RepositoryInfoHeader,
  RepositoryInfoList,
  IssuesList,
  IssuesListItem,
  IssuesListItemInfo,
} from './styles';

interface IRouteParams { repository: string }

const Repository: React.FC = () => {
  const [repository, setRepository] = useState(null);
  const [issues, setIssues] = useState([]);
  const { params: routeParams } = useRouteMatch<IRouteParams>();

  useEffect(() => {
    async function loadPageData() {
      const [{ data: apiRepository }, { data: apiIssues }] = await Promise.all([
        repositoriesApi.get(`repos/${routeParams.repository}`),
        repositoriesApi.get(`repos/${routeParams.repository}/issues`),
      ]);

      setRepository(apiRepository);
      setIssues(apiIssues);
    }

    loadPageData();
  }, [routeParams.repository]);

  return (
    <>
      <Header>
        <img src={logoImg} alt="Github Explorer" />
        <Link to="/dashboard">
          <FiChevronLeft />
          Voltar
        </Link>
      </Header>

      <ContentContainer>
        <RepositoryInfo>
          <RepositoryInfoHeader>
            <img src="https://thispersondoesnotexist.com/image" alt="" />
            <div>
              <strong>oMatheusCaetano/adminPanel</strong>
              <p>Uma turminha da pesada</p>
            </div>
          </RepositoryInfoHeader>

          <RepositoryInfoList>
            <li>
              <strong>1808</strong>
              <span>Stars</span>
            </li>
            <li>
              <strong>48</strong>
              <span>Forks</span>
            </li>
            <li>
              <strong>27</strong>
              <span>Issues abertas</span>
            </li>
          </RepositoryInfoList>
        </RepositoryInfo>

        <IssuesList>
          <IssuesListItem to="/repositories/">
            <IssuesListItemInfo>
              <strong>repository.full_name</strong>
              <p>repository.description</p>
            </IssuesListItemInfo>
            <FiChevronRight />
          </IssuesListItem>

          <IssuesListItem to="/repositories/">
            <IssuesListItemInfo>
              <strong>repository.full_name</strong>
              <p>repository.description</p>
            </IssuesListItemInfo>
            <FiChevronRight />
          </IssuesListItem>

          <IssuesListItem to="/repositories/">
            <IssuesListItemInfo>
              <strong>repository.full_name</strong>
              <p>repository.description</p>
            </IssuesListItemInfo>
            <FiChevronRight />
          </IssuesListItem>
        </IssuesList>
      </ContentContainer>
    </>
  );
};

export default Repository;
