import React, { useState, FormEvent } from 'react';
import { FiChevronRight } from 'react-icons/fi';

import repositoriesApi from '../../services/api/repositories_api';

import logoImg from '../../assets/logo.svg';

import {
  Cotainer,
  Title,
  Form,
  Input,
  SubmitButton,
  ErrorMessage,
  RepositoriesList,
  RepositoriesListItem,
  RepositoriesListItemRepoInfo,
} from './styles';

interface IRepository {
  full_name: string,
  description: string,
  owner: {
    login: string,
    avatar_url: string,
  }
}

const Dashboard: React.FC = () => {
  const [inputText, setInputText] = useState('');
  const [inputError, setInputError] = useState('asdasdas');
  const [repositories, setRepositories] = useState<IRepository[]>([]);

  async function addRepository(event: FormEvent<HTMLFormElement>) {
    event.preventDefault();

    if (!validateInputText()) return;

    try {
      const { data } = await repositoriesApi.get<IRepository>(`/repos/${inputText}`);
      setRepositories([...repositories, data]);
      setInputText('');
    } catch (error) {
      setInputError('Erro ao buscar repositório :/');
    }
  }

  function validateInputText() {
    if (!inputText) {
      setInputError('Por favor, informe um repositório válido. ex: autor/repositório');
      return false;
    }

    return true;
  }

  return (
    <Cotainer>
      <img src={logoImg} alt="Github Explorer" />
      <Title>Explore repositórios do Github</Title>

      <Form onSubmit={addRepository}>
        <Input
          value={inputText}
          onChange={({ target }) => setInputText(target.value)}
          placeholder="Digite o nome do repositório"
        />
        <SubmitButton>Pesquisar</SubmitButton>
      </Form>
      <ErrorMessage>{inputError}</ErrorMessage>

      <RepositoriesList>
        {repositories.map((repository) => (
          <RepositoriesListItem href="asdasd" key={repository.full_name}>
            <img src={repository.owner.avatar_url} alt="" />
            <RepositoriesListItemRepoInfo>
              <strong>{repository.full_name}</strong>
              <p>{repository.description}</p>
            </RepositoriesListItemRepoInfo>
            <FiChevronRight />
          </RepositoriesListItem>
        ))}
      </RepositoriesList>
    </Cotainer>
  );
};

export default Dashboard;
