import React, { useState, InputHTMLAttributes } from 'react';

export enum AvailableIcons {
  logo = 'logo.svg'
}

interface IconProps extends InputHTMLAttributes<HTMLImageElement> {
  iconName: AvailableIcons
}

const Icon: React.FC<IconProps> = ({ iconName }) => {
  const [iconImage, setIconImage] = useState(null);

  function loadIcon(icon: AvailableIcons) {
    import(`@/assets/icons/${icon}`).then((image) => setIconImage(image));
  }

  return (
    <>
      {iconImage && <img src={iconImage} alt="" />}
    </>
  );
};

export default Icon;
