import { styled } from '@/styles/theme';

import Icon from '@/components/atoms/img/Icon';
import Button from '@/components/atoms/button/Button';
import Text from '@/components/atoms/text/Text';

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  min-height: 100vh;
  background: ${(props) => props.theme.colors.primary};
  padding: 30px;

  @media (min-width: ${({ theme }) => theme.breakpoints.xExtraLargeMin}) {
    padding: 30px 10%;
  }
`;

export const HeroContainer = styled.section`
  @media (min-width: ${({ theme }) => theme.breakpoints.mediumMin}) {
    display: flex;
    align-items: center;
    padding: 0 40px;

    div:first-of-type {
      text-align: center;
      display: flex;
      flex-direction: column;
      align-items: flex-end;
    }
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.extraLargeMin}) {
    margin-left: 4%;
    background: green;
  }
`;

export const LogoImg = styled(Icon)`
  width: 100%;

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    display: block;
    width: 70%;
    margin: 0 auto;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.extraLargeMin}) {
    width: 100%;
    margin-top: 35px;
  }
`;

export const StyledSlogan = styled(Text)`
  max-width: 400px;

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    max-width: none;
    text-align: center;
    margin: 15px 0;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.extraLargeMin}) {
    text-align: left;
    max-width: 400px;
  }
`;

export const HeroImg = styled(Icon)`
  display: none;

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    display: block;
    width: 60%;
    margin: 20px auto;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.mediumMin}) {
    max-width: 50%;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.extraLargeMin}) {
    max-width: 50%;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.xExtraLargeMin}) {
    max-width: 35%;
    background: red;
  }
`;

export const ButtonContainer = styled.section`
  padding: 0 15px;

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    padding: 0 20px;

    div:first-of-type {
      display: flex;
      align-items: center;
      margin-top: 30px;
    }
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.mediumMin}) {
    margin-top: 30px;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.extraLargeMin}) {
    display: flex;
    justify-content: space-evenly;
  }
`;

export const StyledButton = styled(Button)`
  width: 100%;
  margin-top: 40px;

  & + Button {
    margin-top: 20px;
    margin-bottom: 15px;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    margin: 0 20px;

    & + Button {
      margin: 0 20px;
    }
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.extraLargeMin}) {
    width: 300px;
  }
`;

export const StyledText = styled(Text)`
  display: flex;
  align-items: center;
  justify-content: center;

  @media (min-width: ${({ theme }) => theme.breakpoints.smallMin}) {
    margin-top: 10px;
    justify-content: flex-end;
    padding-right: 25px;
  }

  @media (min-width: ${({ theme }) => theme.breakpoints.extraLargeMin}) {
    margin-top: 25px;
  }
`;

export const StyledHeartIcon = styled(Icon)`
  margin-left: 10px;
`;
