import React, { useState } from 'react';

import {
  Container,
  Title,
  Form,
  Input,
  SubmitButton,
  TodoList,
  TodoListItem,
  TodoDescription,
} from './styles';

interface TodoItem { title: string, done: boolean }

const [todoItem, setTodoItem] = useState<TodoItem>({ title: '', done: false });

function cleanInput() {
  alert('Clean input called');
}

function mapKeyToAction(event: React.KeyboardEvent) {
  switch (event.key) {
    case 'Escape':
      cleanInput();
      break;
    default:
      break;
  }
}

const App: React.FC = () => (
  <Container>
    <Title>Tarefas</Title>

    <Form>
      <Input onKeyDown={mapKeyToAction} placeholder="Insira uma nova tarefa.." />
      <SubmitButton>Salvar</SubmitButton>
    </Form>

    <TodoList>
      <TodoListItem>
        <input type="checkbox" />
        <TodoDescription>Ir ao shopping</TodoDescription>
      </TodoListItem>
      <TodoListItem>
        <input type="checkbox" />
        <TodoDescription>Ir ao shopping</TodoDescription>
      </TodoListItem>
      <TodoListItem>
        <input type="checkbox" />
        <TodoDescription>Ir ao shopping</TodoDescription>
      </TodoListItem>
    </TodoList>
  </Container>
);

export default App;
